mapper.insert:
  
       SELECT CAP_NOTICE_SEQ.NEXTVAL AS ID FROM DUAL
   
  	INSERT INTO CAP_DELIVERY_NOTICE (
  		NOTICE_NO
  		, NOTICE_TITLE
  		, NOTICE_CONTENT
  		, NOTICE_DIVISION
  	) VALUES (
		#{ntc_No}
  		, #{ntc_Title}
  		, #{ntc_Content}
  		, #{ntc_Division}
  	)

mapper.update:
UPDATE CAP_DELIVERY_NOTICE
SET
	NOTICE_TITLE = #{ntc_Title}
	, NOTICE_CONTENT = #{ntc_Content}
	, NOTICE_DIVISION = #{ntc_Division}
WHERE
	NOTICE_NO = #{ntc_No}
