mapper.insert:
		
     		SELECT CAP_INQUIRY_SEQ.NEXTVAL AS FROM DUAL
 		
 		INSERT INTO CAP_DELIVERY_INQUIRY (
 											INQUIRY_NO
 											, WAYBILL_NUM
 											, NAME
 											, PHONE
 											, INQUIRY_TYPE
 											, ADDRESS
 											, ADDRESS2
 											, PRODUCT_NAME
 											, TITLE
 											, CONTENT
 											
 											, LOGIN_ID
 											
 											)
 											VALUES (
 											#{inquiryNo}
 											, #{waybillNum}
 											, #{name}
 											, #{phone}
 											, #{inquiryType}
 											, #{addr}
 											, #{addr2}
 											, #{productName}
 											, #{title}
 											, #{content}
 											
 											, #{loginId}
 											
 											)

mapper.select:
  	SELECT INQUIRY_TYPE,  TITLE, WAYBILL_NUM, REG_DATE, INQUIRY_STATUS
	FROM CAP_DELIVERY_INQUIRY
	WHERE login_id = #{loginId}
