mapper.update:
UPDATE CAP_DELIVERY_USER
SET SESSION_KEY = #{sessionKey}
	, SESSION_LIMIT = #{sessionLimit}
WHERE USER_ID = #{userId}

mapper.insert:
INSERT INTO CAP_DELIVERY_USER ( USER_ID
                            , USER_NAME
                            , USER_BIRTH
                            , USER_GENDER
                            , USER_PWD
                            , USER_PHONE
                            , USER_ADDR
                            , USER_ADDR2
                            , USER_EMAIL)
                        VALUES( #{user_id}
                            , #{user_name}
                            , #{user_birth}
                            , #{user_gender}
                            , #{user_pwd}
                            , #{user_phone}
                            , #{user_addr}
                            , #{user_addr2}
                            , #{user_email})

mapper.select:
SELECT count(user_id)
FROM cap_delivery_user
WHERE user_id = #{chkId}
